import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    count2: 0,
  };
}
  handleClick() {
   this.setState({count: this.state.count+1});
 }
 handleClick2() {
  this.setState({count: this.state.count2+2});
}

  render(){
    return(
      <div>
      <button className='btn' onClick={()=> {this.handleClick()}}
      onClick={()=>{this.handleClick2()}}>{this.props.name}
      ></button>
      <h1>カウント回数{this.state.count},,{this.state.count2}</h1>
      </div>
    );
  }
}

export default Testbtn;
