import React from 'react';

class Testbtn2 extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    countsum: 0,
    countkati: 0,
    countmake: 0,
    janken: false,
    win: false,
    syobu: '',
    message: '',
    value: 'グー',
  };
}
// ランダムを取得する適当な関数
  getRandomValue(arr){
  return arr[Math.floor(Math.random() * arr.length)];
}
// hondaとじゃんけん勝ちか負けかを判定する。　this.state.value次弟で完成
  vshonda(){
    //これができない
    const playercard = this.state.value;
    //以下の処理は○
    const hondacards = ['グー','チョキ','パー'];
    let count=0;
    for(let i=0;i<9;i++){
      //hondacard取得○
      let hondacard = this.getRandomValue(hondacards);
      //alert(hondacard);
      //一致するならカウント　★一致というのは見た目ではそうだが処理としてはあいこ、勝ち、負けとも取れるため。
      if(this.state.value == hondacard){
      count++;
    }
    }
    if(count>7){
      this.setState({win: true});
      this.setState({syobu: "You Win!"});
      this.setState({message: "やるやん。明日は俺にリベンジさせて"});
      this.setState({countkati: this.state.countkati +1});
    }else{
      this.setState({lose: true});
      this.setState({syobu: "You Lose!!"});
      this.setState({message: "俺の勝ち！なんで負けたのか、明日まで考えといてください"});
      this.setState({countmake: this.state.countmake +1});
    }
  };

  //クリックしたときの反応　不要
  handleClick() {
   this.setState({countsum: this.state.countsum+1});
   //初期化
   this.setState({win: false});
   this.setState({lose: false});
   this.setState({message: ''});

  };
  render(){
    let modal = ''
    //勝利
    if(this.state.win){
      modal = (
        <div>
        <h3>あなたの出したのは{this.state.value}</h3>
        <h2>{this.state.syobu}</h2>
        H○NDA「
        {this.state.message}
        」
        </div>
      );
    }
    //負け
    if(this.state.lose){
      modal = (
        <div>
        <h3>あなたの出したのは{this.state.value}</h3>
        <h2>{this.state.syobu}</h2>
        H○NDA「
        {this.state.message}
        」
        </div>
      );
    }

    return(
    <div>海込みテスト
      {modal}
      <div>
      <span
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      {this.props.name}
      </span>
      <span
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      グー
      </span>
      <span
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      チョキ
      </span>
      <h1>{this.state.countsum}試合中,{this.state.countkati}勝,{this.state.countsum - this.state.countkati}負</h1>
      </div>
    </div>
    );
  }
}
export default Testbtn2;
