import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    janken: false,
    win: false,
    lose: false,
  };
}

  handleClick() {
   this.setState({count: this.state.count+1});
   this.setState({ccc: this.state.ccc+66});
   this.setState({janken: true});

   function choose_at_random(arrayData) {
    var arrayIndex = Math.floor(Math.random() * arrayData.length);
    return arrayData[arrayIndex];
}
   const hondacards = ['パー','チョキ','グー'];
   const hondacard = choose_at_random(hondacards);
 }


