      alert("ｌｋじゃｓｌｋじゃｓだ")
      </div>
  }
      <div>
      <button
      className='btn'
      onClick={()=>{this.handleClick()}}
      >
      パー
      </button>
      <button
      className='btn'
      onClick={()=>{this.handleClick()}}
      >
      グー
      </button>
      <button
      className='btn'
      onClick={()=>{this.handleClick()}}
      >
      チョキ
      </button>

      <h1>カウント回数{this.state.count},{this.state.ccc},hondacard</h1>
      </div>
    </div>


    );
  }
}

export default Testbtn;
