import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    janken: false,
    win: false,
  };
}
// hondaとじゃんけん勝ちか負けかを判定する。
  vshonda(){
    let count=0;
    const playercard = this.state.name
    const hondacards = ['グー','チョキ','パー'];
    let hondacard = 2;
    for(let i=0;i<15;i++){
      //hondacard取得 ★難関

      //一致するならカウント　★一致というのは見た目ではそうだが処理としてはあいこ、勝ち、負けとも取れるため。
      if(this.state.name == hondacard){
      count++;
      //これは大丈夫みたいなので...
    }
    }
    if(count>13){
      this.setState({win: true});
  }else{
    this.setState({lose: true});
  }
  };

  //クリックしたときの反応　不要
  handleClick() {
   this.setState({count: this.state.count+1});
   this.setState({ccc: this.state.ccc+66});
   this.setState({janken: true});
   this.setState({count: 0});
  };

  render(){
    return(
    <div>

      <div>
      <button
      className='btn'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      パー
      </button>
      <button
      className='btn'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      グー
      </button>
      <button
      className='btn'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      チョキ
      </button>

      <h1>カウント回数{this.state.count},{this.state.ccc},hondacard</h1>
      </div>
    </div>
    );
  }
}

export default Testbtn;
