import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
  };
}
  handleClick() {
   this.setState({count: this.state.count+1});
 }

  render(){
    return(
      <div>
      <button className='btn' onClick={()=> {this.handleClick()}}>{this.props.name}</button>
      <h1>カウント回数{this.state.count}</h1>
      </div>
    );
  }
}

export default Testbtn;
