import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    janken: false,
    win: false,
  };
}
// hondaとじゃんけん
  vshonda(){
    let count=0;
    const hondacards = ['グー','チョキ','パー'];
    for(let i=0;i<15;i++){
      //hondacard取得

      //一致するならカウント
      if(this.state.name == hondacard)
      count++;
      //これは大丈夫みたいなので...
      alert(this.state.name);
    }
    if(count>13){
    alert(count);
  }
  // else{
