import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    janken: false,
    win: false,
  };
}
// hondaとじゃんけん
  vshonda(){
    for(let i=0;i<15;i++){
      let count=0;
      count++;
    }if(count>10)
    {
    alert(count);
  }
    //   let hondacard = 1;//ランダム本手の出番抽出
