class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    hondacards: ['パー','チョキ','グー'],
    hondacard: "",
  };
}

  handleClick() {
   this.setState({count: this.state.count+1});
   this.setState({ccc: this.state.ccc+66});
   this.setState({hondacard: choose_at_random(hondacards)});
 }


  render(){
    return(
      <div>
      <button
      className='btn'
      onClick={()=>{this.handleClick()}}
      >
      {this.props.name}
      </button>
      <h1>カウント回数{this.state.count},{this.state.ccc},{this.state.hondacard}</h1>
      </div>
    );
  }
}

export default Testbtn;
