import React from 'react';

class Testbtn extends React.Component{
  constructor(props){
    super(props);
    this.state={
    name: 'honda',
    pcard: '',
    count: 0,
    ccc: 0,
    janken: false,
    win: false,
    syobu: '',
    message: '',
    value: 'グー',
  };
}
// ランダムを取得する適当な関数
  getRandomValue(arr){
  return arr[Math.floor(Math.random() * arr.length)];
}
// hondaとじゃんけん勝ちか負けかを判定する。　this.state.value次弟で完成
  vshonda(){
    const playercard = this.state.value;
    const hondacards = ['グー','チョキ','パー'];
    let count=0;
    for(let i=0;i<15;i++){
      //hondacard取得 ★難関

      let hondacard = this.getRandomValue(hondacards);
      //alert(hondacard);
      //一致するならカウント　★一致というのは見た目ではそうだが処理としてはあいこ、勝ち、負けとも取れるため。
      if(this.state.value == hondacard){
      count++;
      //これは大丈夫みたいなので...
    }
    }
    if(count<4){
      this.setState({win: true});
      this.setState({syobu: "あなたの勝ち"});
      this.setState({message: "やるやん。明日は俺にリベンジさせて"});
    }else{
      this.setState({lose: true});
      this.setState({syobu: "あなたの負け"});
      this.setState({message: "俺の勝ち！なんで負けたのか、明日まで考えといてください"});
    }
  };
  //終わり

  //クリックしたときの反応　不要
  handleClick() {
   this.setState({count: this.state.count+1});
   this.setState({ccc: this.state.ccc+66});
   this.setState({janken: true});
   this.setState({count: 0});
   //初期化
   this.setState({lose: true});
   this.setState({lose: true});
   this.setState({message: ''});

  };
// どこに書いてよいかわからないので、とりあえずここに記述するもの。


  render(){
    let modal = ''
    //勝利
    if(this.state.win){
      modal =(
        <div>
        <h2>{this.state.syobu}</h2>
        H○NDA「
        {this.state.message}
        」
        </div>
      );
    }
    //負け
    if(this.state.syobu){
      modal = (
        <div>
        <h2>{this.state.syobu}</h2>
        H○NDA「
        {this.state.message}
        」
        </div>
      );
    }


    return(
    <div>
      <div>
      {modal}
      <button
      className='btn'
      value = 'パー'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      パー
      </button>
      <button
      className='btn'
      value = 'グー'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      グー
      </button>
      <button
      className='btn'
      value='チョキ'
      onClick={()=>{this.handleClick();this.vshonda()}}
      >
      チョキ
      </button>

      <h1>カウント回数{this.state.count},{this.state.ccc},hondacard</h1>
      </div>
    </div>
    );
  }
}

export default Testbtn;
